import colorgramimport turtle as timport randomt.colormode(255)brush = t.Turtle()brush.shape("circle")brush.pensize(1)brush.speed(0)brush.hideturtle()colors = colorgram.extract('hirst.jpg', 30)colors_list = []for i in range(len(colors)):    color = (colors[i].rgb.r, colors[i].rgb.g, colors[i].rgb.b,)    colors_list.append(color)for x in range(-300, 300, 50):    for y in range(-300, 300, 50):        brush.penup()        brush.setpos(x, y)        brush.color(random.choice(colors_list))        brush.pendown()        brush.stamp()screen = t.Screen()screen.exitonclick()